mapper.select:
SELECT t3.id FROM (
SELECT t1.id, t1.parent_id, t1.state, t2.*, IF(FIND_IN_SET(t1.id, @pids) > 0, @pids := CONCAT(@pids, ',', t1.parent_id), 0) AS isParent FROM ( SELECT id, parent_id, state FROM sys_role WHERE state > 0 ORDER BY parent_id DESC, id DESC ) t1, ( SELECT @pids := #{rid} ) t2
) t3 WHERE t3.isParent != 0 AND t3.id != #{rid} AND t3.state = #{parentRoleState}

SELECT t3.id FROM (
SELECT t1.id, t1.parent_id, t1.state, t2.*, IF(FIND_IN_SET(t1.parent_id, @pids) > 0, @pids := CONCAT(@pids, ',', t1.id), 0) AS isChild FROM ( SELECT id, parent_id, state FROM sys_role WHERE state > 0 ORDER BY parent_id, id ) AS t1, ( SELECT @pids := #{rid} ) AS t2
) t3 WHERE t3.isChild != 0 AND t3.state = #{childrenRoleState}
